using ConsoleDump;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace ExercisesAlgo.Strings
{
    public class MinWindowExec
    {
        public void Execute()
        {
            MinWindow("aa", "aa").Dump();
             MinWindow("ADOBECODEBANC", "ABC").Dump();
            MinWindow("",
                "").Dump();
        }

        public string MinWindow(string s, string t)
        {
            var minstart = 0;
            var minend = s.Length - 1;
            var found = false;
            var l = 0;
            var r = t.Length - 1;
            var search = t.GroupBy(v => v)
                 .ToDictionary(g => g.Key, g => g.Count());
    
            var window = s.Take(t.Length)
                .GroupBy(v => v)
                .ToDictionary(g => g.Key, g => g.Count()); ;

            while (r < s.Length)
            {
//                Console.Write(s.Substring(l , r - l + 1));
//                Console.WriteLine(String.Join("", search));
                if (ExistAllChars(window, search))
                {
//                    Console.Write(" --- ");
//                    Console.WriteLine(s.Substring(l, r - l + 1));
                    if ((r - l) <= (minend - minstart))
                    {
                        minstart = l;
                        minend = r;
                        found = true;
                    }
                    window[s[l]]--;
                    l++;
                }
                else
                {
                    //                   Console.WriteLine(String.Empty);
                    r++;
                    if (r < s.Length)
                    {
                        if (window.ContainsKey(s[r]))
                        {
                            window[s[r]]++;
                        }
                        else
                        {
                            window[s[r]] = 1;
                        }
                    }
                }
            }
            return found? s.Substring(minstart, minend - minstart +1): string.Empty;
        }

        public bool ExistAllChars(Dictionary<char, int> window, Dictionary<char, int> search)
        {
            return
                search
                    .Where(v => v.Value > 0)
                    .All(v => window.ContainsKey(v.Key) && window[v.Key] >= v.Value);
        }

        public bool ExistAllChars(string s, int[] search, int l, int r, int plength)
        {
            var ls = new int[search.Length];
            Array.Copy(search, ls, search.Length);
            var cnt = 0;
            for (int i = l; i <= r; i++)
            {
                if (ls[s[i]] > 0)
                {
                    cnt++;
                }
                if (cnt == plength)
                {
                    return true;
                }
                ls[s[i]]--;
            }
            if (cnt == plength)
            {
                return true;
            }
            return false;
        }

        public bool ExistAllChars2(string s, string t, int l, int r)
        {
            var search = t.ToList();
            for (int i = l; i <= r; i++)
            {
                search.Remove(s[i]);
                if (!search.Any())
                {
                    return true;
                }
            }
            if (!search.Any())
            {
                return true;
            }
            return false;
        }
    }
}
